  // form

import { useState } from "react";



const UserInput=()=>{
  const intialUserInput={
        'currentSaving':10000,
        'yearlyContribution':1200,
        'expectedReturn':7,
        'duration':10
    };

  const useState=()=>{
  const[userInput,setUserInput]= useState({
      'currentSaving':10000,
      'yearlyContribution':1200,
      'expectedReturn':7,
      'duration':10
    });
  }

  const submitHandler=(event)=>{
    event.preventDefault();

    // ...
  };

  const resetHandler=()=>{
    setUserInput(intialUserInput);
  }

  const inputchangeHandler=(input,value)=>{
    setUserInput((prevInput)=>{
        return{
          ...prevInput,
          [input]:value,
        };
    });
  };

    return(
      
    
    <form onSubmit={submitHandler} className="form">
    <div className="input-group">
      <p>
        <label htmlFor="current-savings">Current Savings ($)</label>
        <input onChange={(event)=>inputchangeHandler('current-savings',event.target.value)} type="number" id="current-savings" />
      </p>
      <p>
        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
        <input 
         onChange={(event)=>inputchangeHandler('yearly-contribution',event.target.value)}
        type="number" id="yearly-contribution" />
      </p>
    </div>
    <div className="input-group">
      <p>
        <label htmlFor="expected-return">
          Expected Interest (%, per year)
        </label>
        <input 
         onChange={(event)=>inputchangeHandler('expected-return',event.target.value)}
        type="number" id="expected-return" />
      </p>
      <p>
        <label htmlFor="duration">Investment Duration (years)</label>
        <input type="number"
        onChange={(event)=>inputchangeHandler('duration',event.target.value)}
        id="duration" />
      </p>
    </div>
    <p className="actions">
      <button onClick={resetHandler} type="reset" className="buttonAlt" >
        Reset
      </button>
      <button type="submit" className="button">
        Calculate
      </button>
    </p>
  </form>
);
}
export default UserInput;
