import { useState } from 'react';
import Header from './Component/Header';
import TableResult from './Component/TableResult/TableResult';
import UserInput from './Component/UerInput/UserInput';
function App() {
  const[userInput,setUserInput]= useState(null);

  const calculateHandler = (userInput) => {
    setUserInput(userInput);
  };

  const yearlyData = []; 

    if(userInput){
    let currentSavings = +userInput['current-savings']; 
    const yearlyContribution = +userInput['yearly-contribution'];
    const expectedReturn = +userInput['expected-return'] / 100;
    const duration = +userInput['duration'];

    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
  } 

  return (
    <div>

      <Header/>
      <UserInput onCalculate={calculateHandler}></UserInput>
      {!userInput &&<p>NO INVESTEMENT</p>}
      {userInput &&<TableResult data={yearlyData} intialInvestmenti={userInput['current-savings']} />}

    </div>
  );
}

export default App;
